    private void btnSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalvarActionPerformed
        // TODO add your handling code here:
      String codigo_interno = txtCodigo_interno.getText();
      String descricao = txtDescricao.getText();
      String cod_fabricante = txtCod_fabricante.getText();
      int fabricante_id = Integer.parseInt(txtFabricante_id.getText());
      //String fabricante_id = txtFabricante_id.getText();
      int prazogarantia = Integer.parseInt (txtPrazogarantia.getText());
      String observacoes = txtObservacoes.getText();
      
      
      Produto produto = new Produto();

      produto.setCodigo_interno(codigo_interno);
      produto.setDescricao(descricao);
      produto.setCod_fabricante(cod_fabricante);
      produto.setFabricante_id(fabricante_id);
      produto.setPrazogarantia(prazogarantia);
      produto.setObservacoes(observacoes);
      
          
      ProdutoDAO produtoDAO = new ProdutoDAO();
      produtoDAO.inserir(produto);
     
      txtCodigo_interno.setText("");
      txtDescricao.setText("");
      txtCod_fabricante.setText("");
      txtFabricante_id.setText("");
      txtPrazogarantia.setText("");
      txtObservacoes.setText("");
    }//GEN-LAST:event_btnSalvarActionPerformed

    private void btnAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAlterarActionPerformed
        // TODO add your handling code here:
		int id = Integer.parseInt(txtId.getText());
                String codigo_interno = txtCodigo_interno.getText();
                String descricao = txtDescricao.getText();
                String cod_fabricante = txtCod_fabricante.getText();
                int fabricante_id = Integer.parseInt(txtFabricante_id.getText());
		//Integer.parseInt(txtFabricante_id.getText());
		//String fabricante_id = txtFabricante_id.getText();
		int prazogarantia = Integer.parseInt(txtPrazogarantia.getText());
                String observacoes = txtObservacoes.getText();
                                        
                Produto produto = new Produto();
                produto.setId(id);
                produto.setCodigo_interno(codigo_interno);
                produto.setDescricao(descricao);
                produto.setCod_fabricante(cod_fabricante);
                produto.setFabricante_id(fabricante_id);
		produto.setPrazogarantia(prazogarantia);
                produto.setObservacoes (observacoes);
 
                ProdutoDAO produtoDAO = new ProdutoDAO();
                produtoDAO.editar(produto);
                
                //limpando os campos
                txtId.setText("");
                txtDescricao.setText("");
                txtCodigo_interno.setText("");
		txtCod_fabricante.setText("");
                txtFabricante_id.setText("");
                txtPrazogarantia.setText("");
                txtObservacoes.setText("");                                         

    }//GEN-LAST:event_btnAlterarActionPerformed

