    private void btnPesquisarProdutoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarProdutoActionPerformed
        // TODO add your handling code here:
		int idPesquisa = Integer.parseInt(txtIdPesquisa.getText());  
            
                ProdutoDAO produtoDAO = new ProdutoDAO();
                Produto produto = produtoDAO.getProduto(idPesquisa);
                
                if (produto == null) {
                    JOptionPane.showMessageDialog(this, "Produto não encontrado!");
                } 
                else {
                    txtId.setText(String.valueOf(produto.getId())); //linha adicionada
                    txtCodigo_interno.setText(produto.getCodigo_interno());
                    txtDescricao.setText(produto.getDescricao());
                    txtCod_fabricante.setText(produto.getCod_fabricante());
                    txtFabricante_id.setText(String.valueOf(produto.getFabricante_id()));
                    txtPrazogarantia.setText(String.valueOf(produto.getPrazogarantia()));
                    txtObservacoes.setText(produto.getObservacoes());
                    		    
		    txtIdPesquisa.setText("");
                 }
    }//GEN-LAST:event_btnPesquisarProdutoActionPerformed

