		int cliente_id = Integer.parseInt(txtCliente_id.getText());
		int filial_id = Integer.parseInt(txtFilial_id.getText());
		int produto_id = Integer.parseInt(txtProduto_id.getText());
		//int quant = Integer.parseInt(txtQuant.getText());
		String nf = txtNf.getText();
                String data_nf = txtData_nf.getText();
                String data_receb_produto = txtData_receb_produto.getText();
