		String data_inicio_fase = txtData_inicio_fase.getText();
		String prazo_lim_aceite = txtPrazo_lim_aceite.getText();
		
                Sg sg = new Sg();
		
                sg.setId(id);
		sg.setData_abert_sg(data_abert_sg);
		sg.setCliente_id(cliente_id);
