    private void txtIdPequisaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtIdPequisaActionPerformed
        // TODO add your handling code here:
		int idPesquisa = Integer.parseInt(txtIdPesquisa.getText());  
            
                SgDAO sgDAO = new SgDAO();
                Sg sg = sgDAO.getSg(idPesquisa);
                
                if (sg == null) {
                    JOptionPane.showMessageDialog(this, "SG não encontrada!");
                } 
                else {
                    txtId.setText(String.valueOf(sg.getId())); //linha adicionada
                    txtData_abert_sg.setText(sg.getData_abert_sg());
                    txtCliente_id.setText(String.valueOf(sg.getCliente_id()));
                    txtFilial_id.setText(String.valueOf(sg.getFilial_id()));
		    txtProduto_id.setText(String.valueOf(sg.getProduto_id()));
                    //txtQuant.setText(String.valueOf(sg.getQuant()));
		    txtNf.setText(sg.getNf());
		    txtData_nf.setText(sg.getData_nf());
		    txtData_receb_produto.setText(sg.getData_receb_produto());
		    txtStatus.setText(sg.getStatus());
		    txtFase_processo.setText(sg.getFase_processo());
		    txtData_inicio_fase.setText(sg.getData_inicio_fase());
		    txtPrazo_lim_aceite.setText(sg.getPrazo_lim_aceite());
				    
		    txtIdPesquisa.setText("");
                 }
    }//GEN-LAST:event_txtIdPequisaActionPerformed

